xstateDefinition:
// @ts-nocheck
// Available variables/objects/functions:
// xstate
// - .Machine
// - .interpret
// - .assign
// - .send
// - .sendParent
// - .spawn
// - .raise
// - .actions
//
// Common
// - setInterval, setTimeout, clearInterval, clearTimeout
// - node.send, node.warn, node.log, node.error
// - context.get, context.set
// - flow.get, flow.set
// - env.get
// - util

const pcs = "pcs1";
const { assign } = xstate;
const {Maschine} = pcs;


/***************************
 * Main machine definition * 
 ***************************/
return {
    machine: {
        id: pcs,
        context: {
            power: false,
            maintenance: true,
            automation: false,
            automation_camera: false,
            automation_kinematics: false,
            automation_led: false,
            automation_pump: false,
            automation_daynight: false,
            automation_daylight: false,
            kinematics_motor_enabled: false,
            kinematics_homing_valid: false,
            kinematics_stepper_mode: 0,
            kinematics_homing_started: false,
            kinematics_homing_timeout_counter: 0,
            kinematics_stepper_automation: true,
            kinematics_stepper_automation_timeout_counter: 0,
            camera_sequence_timeout_counter: 0,
            request_timeout_counter: 0,
            request_timeout_retries: 10,
            kinematics_pinionwheel_increased_counter: 0,
            kinematics_pinionwheel_decreased_counter: 0,
            kinematics_pinionwheel_max_missed_counts: 180,
            kinematics_stepper_automation_delay_night_state: -1,
            kinematics_stepper_automation_delay_day_state: -1,

            led_camera_mode: false,
            led_test_mode: false,
        },
        initial: 'main',
        states: {
            main: {
                type: 'parallel',
                on: {
                    POWER_ON: {
                        actions: [assign({ power: true })],
                    },
                    POWER_OFF: {
                        actions: [assign({ power: false })],
                    },
                    MAINTENANCE_ON: {
                        actions: [assign({ maintenance: true })],
                    },
                    MAINTENANCE_OFF: {
                        actions: [assign({ maintenance: false })],
                    },
                    AUTOMATION_ON: {
                        actions: [assign({ automation: true })],
                    },
                    AUTOMATION_OFF: {
                        actions: [assign({ automation: false })],
                    },
                },
                states: {
                    kinematics: {
                        initial: 'init',
                        on: {
                            KINEMATICS_ON: {
                                actions: [assign({ automation_kinematics: true })],
                            },
                            KINEMATICS_OFF: {
                                actions: [assign({ automation_kinematics: false })],
                            },
                            KINEMATICS_MOTOR_ENABLED_ON: {
                                actions: [assign({ kinematics_motor_enabled: true })],
                            },
                            KINEMATICS_MOTOR_ENABLED_OFF: {
                                actions: [assign({ kinematics_motor_enabled: false })],
                            },
                            CAMERA_ON: {
                                actions: [assign({ automation_camera: true })],
                            },
                            CAMERA_OFF: {
                                actions: [assign({ automation_camera: false })],
                            },
                        },
                        states: {
                            init: {
                                after: {
                                    1500: { target: 'check_states' }
                                },
                            },

                            check_states: {
                                entry: [
                                    'getMaintenanceState', 
                                    'getAutomationState', 
                                    'getKinematicsState', 
                                    'getKinematicsMotorState', 
                                    'getPowerState'],
                                after: {
                                    1000: { target: 'check_states' }
                                },
                                always: [
                                    { target: 'running', cond: (context, event) => (
                                            !context.maintenance && 
                                            context.automation && 
                                            context.automation_kinematics && 
                                            context.kinematics_motor_enabled && 
                                            context.power) == true },
                                ],
                            },

                            running: {
                                initial: 'init',
                                on: {
                                    POWER_OFF: {
                                        actions: [assign({ power: false })],
                                        target: 'check_states'
                                    },
                                    MAINTENANCE_ON: {
                                        actions: [
                                            assign({ maintenance: true }), 
                                            'callKinematicsStopMotor'
                                            ],
                                        target: 'check_states'
                                    },
                                    AUTOMATION_OFF: {
                                        actions: [
                                            assign({ automation: false }), 
                                            'callKinematicsStopMotor'
                                            ],
                                        target: 'check_states'
                                    },
                                    KINEMATICS_OFF: {
                                        actions: [
                                            assign({ automation_kinematics: false }), 
                                            'callKinematicsStopMotor'
                                            ],
                                        target: 'check_states'
                                    },
                                    KINEMATICS_MOTOR_ENABLED_OFF: {
                                        actions: [
                                            assign({ kinematics_motor_enabled: false }),
                                            'callKinematicsStopMotor'
                                            ],
                                        target: 'check_states'
                                    },
                                },
                                states: {
                                    init: {
                                        always: [
                                            { target: 'check_homing_valid'},
                                        ],
                                        onExit: ['callKinematicsStopMotor'], 
                                    },

                                    check_homing_valid: {
                                        entry: ['getKinematicsHomingValidState'],
                                        after: {
                                            1000: { target: 'check_homing_valid' }
                                        },
                                        on: {
                                            KINEMATICS_HOMING_VALID_ON: {
                                                actions: [assign({ kinematics_homing_valid: true })],
                                                target: 'init_automation'
                                            },
                                            KINEMATICS_HOMING_VALID_OFF: {
                                                actions: [assign({ kinematics_homing_valid: false })],
                                                target: 'init_global_homing'
                                            },
                                        },
                                    },

                                    init_global_homing: {
                                        entry: [
                                            'callKinematicsGlobalHoming', 
                                            assign({ kinematics_homing_timeout_counter: 0 })
                                            ],
                                        always: [
                                            { target: 'check_global_homing_finished' },
                                        ],
                                    },

                                    check_global_homing_finished: {
                                        entry: ['getKinematicsHomingValidState'],
                                        after: {
                                            1000: { target: 'check_global_homing_finished' }
                                        },
                                        on: {
                                            KINEMATICS_HOMING_VALID_ON: {
                                                actions: [assign({ kinematics_homing_valid: true })],
                                                target: 'init_automation'
                                            },
                                        },
                                        always: [
                                            { target: 'init', 
